import {useEffect, useState} from 'react';
import axios from 'axios';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const useRowStyles = makeStyles({
    root: {
        '& > *': {
            borderBottom: 'unset',
        },
    },
});

function Row(props) {
    const { row } = props;
    const [open, setOpen] = React.useState(false);
    const classes = useRowStyles();
  
    return (
        <React.Fragment>
            <TableRow className={classes.root}>
                <TableCell>
                    <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                    {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
                    </IconButton>
                </TableCell>
                <TableCell component="th" scope="row">
                    {row.email}
                </TableCell>
                <TableCell align="right">{row.first_name}</TableCell>
                <TableCell align="right">{row.last_name}</TableCell>
                <TableCell align="right">This is the education</TableCell>
                <TableCell align="right">This is the experience</TableCell>
            </TableRow>
            <TableRow>
                <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
                    <Collapse in={open} timeout="auto" unmountOnExit>
                    <Box margin={1}>
                        <Typography variant="h6" gutterBottom component="div">
                            Proposed tutoring subjects
                        </Typography>
                        <Table size="small" aria-label="purchases">
                        <TableHead>
                            <TableRow>
                            <TableCell>Subject</TableCell>
                            <TableCell>Approve</TableCell>
                            <TableCell>Reject</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {row.subjects.map((subject, index) => (
                                <TableRow key={index}>
                                    <TableCell component="th" scope="row">
                                        {subject}
                                    </TableCell>
                                    <TableCell><button style={{cursor: "pointer"}}>approve</button></TableCell>
                                    <TableCell><button style={{cursor: "pointer"}}>reject</button></TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                        </Table>
                    </Box>
                    </Collapse>
                </TableCell>
            </TableRow>
        </React.Fragment>
    );
}

export default function AdminDashboard(props) {
    const [tutorsApplications, setTutorsApplications] = useState([]);
    const {setDisplayNavbar, setUser} = props;
    setDisplayNavbar(true);
    useEffect(() => {
        const ac = new AbortController();
        axios.get('/auth/isLoggedIn')
            .then(res => res.data)
            .catch(err => console.log(err))
            .then(res => {
                if (res.isLoggedIn) {
                    const user = res.user;
                    setUser(user);
                    axios.get('/admin/tutorsApplications')
                        .then(res => res.data)
                        .catch(err => console.log(err))
                        .then(res => {
                            if (res.success) {
                                setTutorsApplications(res.result);
                            } else {
                                alert("Failed to get tutors applications");
                            }
                        })
                } else {
                    window.open('/', '_self');
                }
            })
        return () => {
            ac.abort();
        }
    }, [setUser]);
    return <div className="wrapper">
        <div className="sidebar">
            <h2>Admin</h2>
            <ul>
                <li>Tutors applications</li>
                <li>Add new admin</li>
                <li>Delete / ban user account</li>
                <li>Remove from tutor</li>
                <li>Report bugs</li>
            </ul>
        </div>
        <div className="content">
            <div className="box">
                <div className="box_top">
                    <h2>Tutors Applications</h2>
                </div>
                <div className="box_bottom">
                    <TableContainer component={Paper}>
                        <Table aria-label="collapsible table">
                            <TableHead>
                                <TableRow>
                                    <TableCell />
                                    <TableCell>Email</TableCell>
                                    <TableCell align="right">First name</TableCell>
                                    <TableCell align="right">Last name</TableCell>
                                    <TableCell align="right">Educations</TableCell>
                                    <TableCell align="right">Experiences</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {tutorsApplications.map((application) => (
                                    <Row key={application.applicant_id} row={application} />
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </div>
            </div>
            <div className="box">
                <div className="box_top">
                    <h2>Add new admin</h2>
                </div>
                <div className="box_bottom">
                    <TextField id="outlined-basic" label="Enter user's email" variant="outlined" />
                    <Button style={{
                        height: "56px",
                        marginLeft: "1em"
                    }} variant="outlined" color="secondary">
                        Add
                    </Button>
                </div>
            </div>
            <div className="box">
                <div className="box_top">
                    <h2>Delete user account</h2>
                </div>
