import axios from 'axios';
import {useEffect, useRef, useState} from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        width: 200,
        marginTop: "1em",
        color: "white"
    }
}));

export default function TutorDashboard(props) {
    const classes = useStyles();
    const addScheduleRef = useRef();
    const {setDisplayNavbar, setUser} = props;
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState(""); 
    const [schedules, setSchedules] = useState([]);
    const [daynumbers, setDaynumbers] = useState([]);

    setDisplayNavbar(true);
    useEffect(() => {
        const ac = new AbortController();
        axios.get('/auth/isLoggedIn')
            .then(res => res.data)
            .catch(err => console.log(err))
            .then(res => {
                if (res.isLoggedIn) {
                    const user = res.user;
                    setUser(user);
                    const dates = [];
                    const temp = [];
                    const curr_day = new Date();
                    for (var i = 0; i < 7; i++) {
                        if (i === curr_day.getDay()) {
                            dates.push(curr_day.getFullYear() + "-" + (curr_day.getMonth() + 1) + "-" + curr_day.getDate());
                            temp.push(curr_day.getDate());
                        } else {
                            const other_day = new Date(curr_day.getTime());
                            if (i < curr_day.getDay()) {
                                other_day.setDate(curr_day.getDate() - (curr_day.getDay() - i));
                            } else {
                                other_day.setDate(curr_day.getDate() + (i - curr_day.getDay()));
                            }
                            temp.push(other_day.getDate());
                            dates.push(other_day.getFullYear() + "-" + (other_day.getMonth() + 1) + "-" + other_day.getDate())
                        }
                    }
                    setDaynumbers(temp);
                    const formData = new FormData();
                    for (var i = 0; i < 7; i++) {
                        formData.append('dates[]', dates[i]);
                    }
                    axios({
                        method: 'post',
                        url: '/tutor/getSchedule',
                        data: formData,
                        headers: {'Content-Type': 'multipart/form-data'}
                    })
                    .then(res => res.data)
                    .catch(err => console.log(err))
                    .then(res => {
                        if (res.success) {
                            setSchedules(res.result);
                        } else {
                            alert('Failed');
                        }
                    })
                } else {
                    window.open('/', '_self');
                }
            })
        return () => {
            ac.abort();
        }
    }, [setUser]);

    function handleAddSchedule() {
        const formData = new FormData(addScheduleRef.current);
        setTitle("");
        setDescription("");
        axios({
            method: 'post',
            url: '/tutor/addNewSchedule',
            data: formData,
            headers: {'Content-Type': 'multipart/form-data'}
        })
        .then(res => res.data)
        .catch(err => console.log(err))
        .then(res => {
            if (res.success) {
                alert('Success');
            } else {
                alert('Failed');
            }
        })
        .catch(err => console.log(err));
    }

    return <div className="wrapper">
        <div className="sidebar">
            <ul>
                <li onClick={() => {
                    window.location.href = "#tutoringSchedule";
                }}><a href="#tutoringSchedule">Tutoring schedule</a></li>
                <li onClick={() => {
                    window.location.href = "#addNewSchedule";
                }}><a href="#addNewSchedule">Add new schedule</a></li>
            </ul>
        </div>
        <div className="content" style={{overflowX: "hidden"}}>
            <div className="box">
                <a name="tutoringSchedule"></a>
                <div className="box_top">
                    <h2>Tutoring schedule</h2>
                </div>
                <div className="box_bottom">
                    <Button style={{
                        height: "56px",
                    }} variant="outlined" color="secondary">
                        Prev
                    </Button>
                    <Button style={{
                        height: "56px",
                        marginLeft: "1em"
                    }} variant="outlined" color="secondary">
                        Next
                    </Button><br /><br />
                    <div className="calendar_container">
                        <div className="calendar_header">
                            <ul className="weekdays">
                                <li>SUN</li>
                                <li>MON</li>
                                <li>TUE</li>
                                <li>WED</li>
                                <li>THU</li>
                                <li>FRI</li>
                                <li>SAT</li>
                            </ul>

                            <ul className="daynumbers">
                                {daynumbers.map(daynumber => {
                                    return 
                                })}
                                <li>1</li>
                                <li>2</li>
                                <li>3</li>
                                <li>4</li>
                                <li>5</li>
                                <li>6</li>
                                <li>7</li>
                            </ul>
                        </div>
                        <div className="timeslots_container">
                            <ul className="timeslots">
                                <li>GMT+7</li>
                                <li>01:00</li>
                                <li>02:00</li>
                                <li>03:00</li>
                                <li>04:00</li>
                                <li>05:00</li>
                                <li>06:00</li>
                                <li>07:00</li>
                                <li>08:00</li>
                                <li>09:00</li>
                                <li>10:00</li>
                                <li>11:00</li>
                                <li>12:00</li>
                                <li>13:00</li>
                                <li>14:00</li>
                                <li>15:00</li>
                                <li>16:00</li>
                                <li>17:00</li>
                                <li>18:00</li>
                                <li>19:00</li>
                                <li>20:00</li>
                                <li>21:00</li>
                                <li>22:00</li>
                                <li>23:00</li>
                            </ul>
                        </div>

                        <div className="event_container">
                            {schedules.map(schedule => {
                                console.log('setting up schedu')
                                const num_start_time = parseInt(schedule.start_time.split(":")[0]);
                                const num_end_time = schedule.end_time.split(":")[0];
                                const grid_row = (num_start_time * 4) + 1;
                                const slotStyle = {
                                    gridRow: String(grid_row),
                                    gridColumn: 1 + new Date(schedule.date).getDay(),
                                    height: String((num_end_time - num_start_time) * 60) + "px"
                                }
                                return <div className="slot" style={slotStyle}>
                                    <div className="event_status">{schedule.title}<br /><br />{schedule.description}</div>
                                </div>
                            })}
                        </div>
                    </div>
                </div>
            </div>
            <div className="box">
                <a name="addNewSchedule"></a>
                <div className="box_top">
                    <h2>Add new schedule</h2>
                </div>
                <div className="box_bottom">
                    <form ref={addScheduleRef}>
                        <TextField 
                            id="outlined-basic" 
                            label="Enter title" 
                            name="title"
                            value={title}
                            onChange={(e) => {
                                setTitle(e.target.value);
                            }}
                            variant="outlined"/><br />
                        <TextField
                            placeholder="Description"
                            multiline
                            rows={4}
                            rowsMax={4}
                            variant="outlined"
                            name="description"
                            value={description}
                            onChange={(e) => {
                                setDescription(e.target.value);
                            }}
                            style={{marginTop: "1em", width: "50%"}}
                        />
                        <div className={classes.container} noValidate>
                            <TextField
                                name="startTime"
                                id="startTime"
                                variant="outlined"
                                label="Start time"
                                type="datetime-local"
                                defaultValue={new Date().toString()}
                                className={classes.textField}
                                InputLabelProps={{
                                shrink: true,
                                className: classes.input
                                }}
                                InputProps={{ className: classes.input }}
                            />
                            <TextField
                                style={{marginLeft: '1em'}}
                                name="endTime"
                                id="endTime"
                                variant="outlined"
                                label="End time"
                                type="datetime-local"
                                defaultValue={new Date().toString()}
                                className={classes.textField}
                                InputLabelProps={{
                                shrink: true,
                                className: classes.input
                                }}
                                InputProps={{ className: classes.input }}
                            />
                        </div><br />
                    </form>
                    <Button style={{
                        height: "56px",
                    }} onClick={handleAddSchedule} variant="outlined" color="secondary">
                        Add
                    </Button>
                </div>
            </div>
            
        </div>
    </div>
}
