import axios from 'axios';
import {useEffect, useState} from 'react';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import ReactAvatarEdit from 'react-avatar-edit';
import { makeStyles } from '@material-ui/core/styles';
import ReactAvatar from '@material-ui/core/Avatar';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Autocomplete from '@material-ui/lab/Autocomplete';

const useStyles = makeStyles((theme) => ({
    large: {
        width: theme.spacing(14),
        height: theme.spacing(14),
    },
    formControl: {
        marginTop: "1em",
        minWidth: 120,
    },
    formControl2: {
        marginTop: "1em",
        marginLeft: "1em",
        minWidth: 120,
    },
    formControl3: {
        marginLeft: "1em",
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

export default function ApplyTutor(props) {
    const {setDisplayNavbar, setUser} = props;
    const [localUser, setLocalUser] = useState();
    const classes = useStyles();
    
    setDisplayNavbar(true);
    useEffect(() => {
        const ac = new AbortController();
        axios.get('/auth/isLoggedIn')
            .then(res => res.data)
            .catch(err => console.log(err))
            .then(res => {
                if (res.isLoggedIn) {
                    const user = res.user;
                    setUser(user);
                    setLocalUser(user);
                } else {
                    window.open('/', '_self');
                }
            })
        return () => {
            ac.abort();
        }
    }, [setUser]);

    const [avatarData, setAvatarData] = useState({
        preview: null,
        src: null,
        file: null,
        currentExtension: null,
        originalFile: null
    });

    const [uploadingNewFile, setUploadingNewFile] = useState(false);

    function onClose() {
        setUploadingNewFile(false);
        setAvatarData({preview: null, src: null, file: null, originalFile: null});
    }
    
    async function onCrop(preview) {
        setAvatarData(prevData => {
            return {...prevData, preview};
        })
        const currentExtension = avatarData.currentExtension;
        const file = await urltoFile(preview, 'userImage.' + currentExtension, 'image/' + currentExtension)
        setAvatarData(prevData => {
            return {...prevData, file: file};
        })
    }
    
    function onBeforeFileLoad(event) {
        const re = /\.(gif|jpe?g|tiff?|png|webp|bmp)$/i;
        const file = event.target.files[0];
        if (file.size > 1000000000000000) {
            event.target.value = "";
            alert("File size is too big.")
        } else if (!re.test(file.name)) {
            alert("File extension is not allowed.")
        } else {
            setUploadingNewFile(true);
            const separator = /(?:\.([^.]+))?$/;
            setAvatarData(prevData => {
                return {...prevData, currentExtension: separator.exec(file.name)[1], originalFile: file};
            })
        }
    }
    
    function urltoFile(url, filename, mimeType){
        return (fetch(url)
            .then(function(res){return res.arrayBuffer();})
            .then(function(buf){return new File([buf], filename,{type:mimeType});})
        );
    }

    const [gender, setGender] = useState(1);
    const [country, setCountry] = useState("Indonesia");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [headline, setHeadline] = useState("");
    const [selfDescription, setSelfDescription] = useState("");
    const [chosenSubjects, setChosenSubjects] = useState([]);
    const [educations, setEducations] = useState([]);

    const [experiences, setExperiences] = useState([{
        company: "",
        jobTitle: "",
        fromYear: 2021,
        toYear: 2021,
        description: ""
    }]);

    const years = [];
    for (var i = 1980; i < 2051; i++) {
        years.push(i);
    }

    const availableSubjects = [
        "Mathematics",
        "Physics",
        "Computer science",
        "Biology",
        "Chemistry",
        "Music",
        "Guitar",
        "Piano",
        "Drum",
        "Saxophone",
        "Vocal",
        "Robotics",
        "Dance",
        "Culinary",
        "Politics",
        "Photography",
        "Machine Learning",
        "Entrepreneurship",
        "Aerodynamics",
        "React",
        "Redux",
        "MongoDB",
        "SQL",
        "PostGreSql",
        "Astronomy",
        "Korean",
        "English",
        "Mandarin",
        "Statistical Mechanics",
        "Data Science",
        "Linear Algebra",
        "iOS Development",
        "Android Development",
        "HTML",
        "Web Development",
        "Calculus",
        "Organic Chemistry",
        "Irish",
        "Chemical Engineering",
        "Agricultural Economics",
        "Civil Engineering",
        "Software Testing",
        "Data Visualization",
        "Economics",
        "Business",
        "User Experience",
        "Zoology",
        "Marine Biology",
        "Aerospace Engineering",
        "Mechanical Engineering",
        "Fashion",
        "Unix",
        "JavaScript",
        "Python",
        "Java",
        "C++",
        "Ruby",
        "Go",
        "Health and Science",
        "Music Theory",
        "Martial Arts",
        "Game Development",
        "Microsoft Word",
        "Microsoft Excel",
        "REST API",
        "Photoshop",
        "Yoga",
        "Pole Dance",
        "Philosophy",
        "LaTeX",
        "German",
        "Arabic",
        "Criminal Justice",
        "Parenting",
        "Indonesian History",
        "Electrical Engineering",
        "MatLab",
        "Epidemiology",
        "Microbiology",
        "Discrete Math",
        "Creative Writing",
        "Stand up comedy",
        "Industrial Engineering",
        "Autism",
        "Tourism",
        "Physiology",
        "LinkedIn",
        "Neuroscience",
        "Animation",
        "Interior design",
        "Blockchain",
        "Electronics Engineering",
        "TensorFlow",
        "Journalism",
        "Psychology"
    ]

    return <div className="wrapper">
        <div className="sidebar">
            <h2>Apply</h2>
            <ul>
                <li><a href="#personalInformation">Personal information</a></li>
                <li><a href="#subjects">Subjects</a></li>
                <li><a href="#education">Education</a></li>
                <li><a href="#experience">Experience</a></li>
                <li><a href="#policyAgreement">Policy Agreement</a></li>
                <li><a href="#submitApplication">Submit Application</a></li>
            </ul>
        </div>
        <div className="content">
            <div className="box">
                <a name="personalInformation"></a>
                <div className="box_top">
                    <h2>Personal information</h2>
                </div>
                <div className="box_bottom">
                    <form>
                        <TextField 
                            id="outlined-basic" 
                            label="Phone number" 
                            variant="outlined"
                            value={phoneNumber}
                            onChange={(event) => {
                                setPhoneNumber(event.target.value);
                            }} />
                        <TextField 
                            style={{marginLeft: "1em"}} 
                            id="outlined-basic" 
                            label="Headline" 
                            variant="outlined"
                            value={headline}
                            onChange={(event) => {
                                setHeadline(event.target.value);
                            }} />
                        <br />
                        <FormControl variant="outlined" className={classes.formControl}>
                            <InputLabel id="demo-simple-select-outlined-label">Gender</InputLabel>
                            <Select
                                labelId="demo-simple-select-outlined-label"
                                id="demo-simple-select-outlined"
                                value={gender}
                                onChange={(event) => {
                                    setGender(event.target.value);
                                }}
                                label="Gender"
                            >
                                <MenuItem value={1}>Male</MenuItem>
                                <MenuItem value={0}>Female</MenuItem>
                                <MenuItem value={-1}>Prefer not to say</MenuItem>
                            </Select>
                        </FormControl>
                        <FormControl variant="outlined" className={classes.formControl2}>
                            <InputLabel id="demo-simple-select-outlined-label">Country</InputLabel>
                            <Select
                                labelId="demo-simple-select-outlined-label"
                                id="demo-simple-select-outlined"
                                value={country}
                                onChange={(event) => {
                                    setCountry(event.target.value);
                                }}
                                label="Country"
                            >   
                                <MenuItem value={"Australia"}>Australia</MenuItem>
                                <MenuItem value={"Canada"}>Canada</MenuItem>
                                <MenuItem value={"Indonesia"}>Indonesia</MenuItem>
                                <MenuItem value={"Ireland"}>Ireland</MenuItem>
                                <MenuItem value={"Malaysia"}>Malaysia</MenuItem>
                                <MenuItem value={"Singapore"}>Singapore</MenuItem>
                                <MenuItem value={"United Kingdom"}>United Kingdom</MenuItem>
                                <MenuItem value={"United States of America"}>United States of America</MenuItem>
                                <MenuItem value={"Others"}>Others</MenuItem>
                            </Select>
                        </FormControl>
                        <br />
                        <TextField
                            placeholder="Description about yourself"
                            multiline
                            rows={4}
                            rowsMax={4}
                            variant="outlined"
                            value={selfDescription}
                            onChange={(event) => {
                                setSelfDescription(event.target.value);
                            }}
                            style={{marginTop: "1em", width: "61.6%"}}
                        />
                    </form>
                </div>
            </div>
            <div className="box">
                <a name="subjects"></a>
                <div className="box_top">
                    <h2>Subjects</h2>
                </div>
                <div className="box_bottom">
                <Autocomplete
                    onChange={(event, value) => {
                        setChosenSubjects(value);
                    }}
                    multiple
                    limitTags={2}
                    id="multiple-limit-tags"
                    options={availableSubjects}
                    getOptionLabel={(option) => option}
                    renderInput={(params) => (
                        <TextField {...params} variant="outlined" label="Available subjects" placeholder="Favorites" />
                    )}
                />
                </div>
            </div>
            <div className="box">
                <a name="education"></a>
                <div className="box_top">
                    <h2>Education</h2>
                </div>
                <div className="box_bottom">
                    <Button 
                        variant="outlined" 
                        color="secondary"
                        id="specialAddMoreEducation"
                        onClick={() => {
                            setEducations(prevData => {
                                document.getElementById("specialAddMoreEducation").style.display = "none";
                                return [...prevData, {
                                    school: "",
                                    fromYear: 2021,
                                    toYear: 2021,
                                    degree: "",
                                    fieldOfStudy: "",
                                    description: ""
                                }]
                            })
                        }}>
                        add more education
                    </Button>
                    {educations.map((education, index) =>{
                        return <div><TextField 
                            id="outlined-basic" 
                            label="School" 
                            variant="outlined"
                            value={education.school}
                            onChange={(event) => {
                                setEducations(prevData => {
                                    const temp = [];
                                    for (var i = 0; i < prevData.length; i++) {
                                        if (i === index) {
                                            temp.push({
                                                ...prevData[index],
                                                school: event.target.value
                                            })
                                        } else {
                                            temp.push(prevData[i]);
                                        }
                                    }
                                    return temp;
                                })
                            }} />
                        <FormControl variant="outlined" className={classes.formControl3}>
                            <InputLabel id="demo-simple-select-outlined-label">From year</InputLabel>
                            <Select
                                labelId="demo-simple-select-outlined-label"
                                id="demo-simple-select-outlined"
                                value={education.fromYear}
                                onChange={(event) => {
                                    setEducations(prevData => {
                                        const temp = [];
                                        for (var i = 0; i < prevData.length; i++) {
                                            if (i === index) {
                                                temp.push({
                                                    ...prevData[index],
                                                    fromYear: event.target.value
                                                })
                                            } else {
                                                temp.push(prevData[i]);
                                            }
                                        }
                                        return temp;
                                    })
                                }}
                                label="From Year"
                            >   
                                {years.map(year => {
                                   return <MenuItem value={year}>{year}</MenuItem> 
                                })}
                            </Select>
                        </FormControl>
                        <FormControl variant="outlined" className={classes.formControl3}>
                            <InputLabel id="demo-simple-select-outlined-label">To year</InputLabel>
                            <Select
                                labelId="demo-simple-select-outlined-label"
                                id="demo-simple-select-outlined"
                                value={education.toYear}
                                onChange={(event) => {
                                    setEducations(prevData => {
                                        const temp = [];
                                        for (var i = 0; i < prevData.length; i++) {
                                            if (i === index) {
                                                temp.push({
                                                    ...prevData[index],
                                                    toYear: event.target.value
                                                })
                                            } else {
                                                temp.push(prevData[i]);
                                            }
                                        }
                                        return temp;
                                    })
                                }}
                                label="To Year"
                            >   
                                {years.map(year => {
                                   return <MenuItem value={year}>{year}</MenuItem> 
                                })}
